mapper.select:
SELECT * from user

    
        name = #{name}
    
    
        AND status = #{status}

mapper.update:
update user

    
        ${field} = ${field} + #{offset} WHERE id = #{id}
    
    
        ${field} = ${field} - #{offset} WHERE id = #{id} AND ${field} > #{offset}
    
    
        ${field} = ${field} * #{offset} WHERE id = #{id} AND ${offset} != 0
    
    
        ${field} = ${field} / #{offset} WHERE id = #{id} AND ${offset} != 0
